hudson.tasks.BatchFile.command:
setlocal enabledelayedexpansion
SET modified=%WORKSPACE:/=\%
if "%P4CLEANWORKSPACE%" == "true" (

echo "Removing %modified%"
rd /s /q %modified%
REM jenkins-${NODE_NAME}-${JOB_NAME}-${BRANCH_NAME}
echo 9R1Y9fE4$PSH|p4 -u tableuser -p perforce.spirentcom.com:1666 login
p4 -u tableuser -p perforce.spirentcom.com:1666 -c jenkins-%NODE_NAME%-%JOB_NAME%-%BRANCH_NAME% sync //jenkins-%NODE_NAME%-%JOB_NAME%-%BRANCH_NAME%/...#none > clean.txt
)
