hudson.tasks.Shell.command:
#!/bin/bash
for i in $(seq 1 3) do if [ -f /mnt/builds/jenkins/il_changeling_yocto-${BRANCH_NAME}_promoting.txt ]; then echo "Promotion is in progress, wait for 5 minutes" sleep 300 elif (( $i < 3 )) ; then echo "No promotion is in progress, proceeding with packaging" break fi done
echo "Packaging started" > /mnt/builds/jenkins/il_changeling_yocto-${BRANCH_NAME}_packaging.txt echo Copying il_changeling_yocto build artifacts from /mnt/builds/jenkins/il_changeling_yocto-${BRANCH_NAME}/build-packaging/ ls -algh /mnt/builds/jenkins/il_changeling_yocto-${BRANCH_NAME}/build-packaging/*.txt filename=`ls /mnt/builds/jenkins/il_changeling_yocto-${BRANCH_NAME}/build-packaging/il_changeling_yocto-${BRANCH_NAME}_[0-9]*.txt` changelist=${filename: -10:6} content=`cat $filename` content=${content//\"/} buildno=${content#*number} echo $changelist il_changeling_yocto $buildno >> .changelists mkdir -p $WORKSPACE/build/il/packages cp -r -v /mnt/builds/jenkins/il_changeling_yocto-${BRANCH_NAME}/build-packaging/* $WORKSPACE/build/il/packages cp $WORKSPACE/build/il/packages/fsimg/rawimage.map $WORKSPACE/build/il/packages/fsimg/changeling_rawimage.map echo "Finished copying il_changeling files" rm -f /mnt/builds/jenkins/il_changeling_yocto-${BRANCH_NAME}_packaging.txt

