#!/bin/bash
for i in $(seq 1 3) do if [ -f /mnt/builds/jenkins/il_qemu_yocto-${BRANCH_NAME}_promoting.txt ]; then echo "Promotion is in progress, wait for 5 minutes" sleep 300 elif (( $i < 3 )) ; then echo "No promotion is in progress, proceeding with packaging" break fi done
echo "Packaging started" > /mnt/builds/jenkins/il_qemu_yocto-${BRANCH_NAME}_packaging.txt echo Copying il_qemu build artifacts from /mnt/builds/jenkins/il_qemu_yocto-${BRANCH_NAME}/build-packaging/ ls -algh /mnt/builds/jenkins/il_qemu_yocto-${BRANCH_NAME}/build-packaging/*.txt filename=`ls /mnt/builds/jenkins/il_qemu_yocto-${BRANCH_NAME}/build-packaging/il_qemu_yocto-${BRANCH_NAME}_[0-9]*.txt` changelist=${filename: -10:6} content=`cat $filename` content=${content//\"/} buildno=${content#*number} echo $changelist il_qemu_yocto $buildno >> .changelists # cp /mnt/builds/jenkins/il_qemu_yocto-${BRANCH_NAME}/build-packaging/rawimg/yocto* $WORKSPACE/build/il/packages/rawimg cp -v /mnt/builds/jenkins/il_qemu_yocto-${BRANCH_NAME}/build-packaging/qemu*.tgz $WORKSPACE/build/il/packages/rawimg echo "Finished copying il_qemu files" rm -f /mnt/builds/jenkins/il_qemu_yocto-${BRANCH_NAME}_packaging.txt

export RSYNCUSER=scm export BUILD_OUTPUT_DIR=$WORKSPACE export MARTIN_ROOT=/spirent/data/archive/pv/TestCenter export MARTIN=martin.spirentcom.com export RAW_BLDSTR=${BLL_BUILD_NUMBER}${BRANCH_ID} printf -v BLDNUMBER "%04d" $RAW_BLDSTR export BLDNBR=${STC_MAJ_MIN_VERSION}.${BLDNUMBER} export BUILD_DIR=$MARTIN_ROOT/$STC_MAJ_MIN_VERSION/Engineering_Builds/IL/$BLDNBR
# -- rename build output files in fsimg directory cd $BUILD_OUTPUT_DIR/build/il/packages/fsimg for i in $(ls *testcenter*yoctovm*); do newname=`echo $i | sed 's/_yoctovm//'`; echo Renaming: $i to $newname; mv $i $newname; done;
for i in $(ls yocto_cloud*); do newname=`echo $i | sed 's/yocto_cloud/qemu/'`; echo Renaming: $i to $newname; mv $i $newname; done;
# -- create rawimage.map file cd $BUILD_OUTPUT_DIR/build/il/packages/fsimg mv rawimage.map rawimage.map.yocto echo "DX-10G-S8 512 changeling_64-${BLDNBR}-fs" > rawimage.map echo "SPT-C100-YOCTO 512 yocto-${BLDNBR}-fs" >> rawimage.map echo "SPT-C1-YOCTO 512 yocto-${BLDNBR}-fs" >> rawimage.map
if ls *stc_appliance* 1> /dev/null 2>&1; then echo "SPT-C100 512 stc_appliance-${BLDNBR}-fs" >> rawimage.map echo "SPT-C100-S3 512 stc_appliance-${BLDNBR}-fs" >> rawimage.map echo "SPT-C1 512 stc_appliance-${BLDNBR}-fs" >> rawimage.map echo "SPT-C50 512 stc_appliance-${BLDNBR}-fs" >> rawimage.map echo "SPT-S50 512 stc_appliance-${BLDNBR}-fs" >> rawimage.map else echo "SPT-C100 512 appliance-${BLDNBR}-fs" >> rawimage.map echo "SPT-C100-S3 512 appliance-${BLDNBR}-fs" >> rawimage.map echo "SPT-C1 512 appliance-${BLDNBR}-fs" >> rawimage.map echo "SPT-C50 512 appliance-${BLDNBR}-fs" >> rawimage.map echo "SPT-S50 512 appliance-${BLDNBR}-fs" >> rawimage.map fi
echo "STC-ANY-V1 512 stca-${BLDNBR}-fs" >> rawimage.map echo "SPT-ANYWHERE 512 stca-${BLDNBR}-fs" >> rawimage.map echo "SPT-QEMU 512 qemu-${BLDNBR}-fs" >> rawimage.map echo "CPU-5001B 512 qemu-${BLDNBR}-fs" >> rawimage.map echo "SPT-VIRTUAL 512 qemu-${BLDNBR}-fs" >> rawimage.map echo "SPT-ESXI 512 qemu-${BLDNBR}-fs" >> rawimage.map echo "VM-1G-V1 512 qemu-${BLDNBR}-fs" >> rawimage.map echo "CPU-5004A 512 iceboy-${BLDNBR}-fs" >> rawimage.map
# -- rename build output files in rawimg directory cd $BUILD_OUTPUT_DIR/build/il/packages/rawimg for i in $(ls yocto_cloud*); do newname=`echo $i | sed 's/yocto_cloud/qemu/'`; echo Renaming: $i to $newname; mv $i $newname; done;
# -- rename yocto*.tgz build output files in rawimg directory cd $BUILD_OUTPUT_DIR/build/il/packages/rawimg for i in $(ls yocto*tgz); do newname=`echo $i | sed "s/[0-9]\.[0-9][0-9]\.[0-9][0-9][0-9][0-9]/${BLDNBR}/"`; if [ ! -f $newname ]; then echo Renaming: $i to $newname; mv $i $newname; fi done;
# -- transfer (via rsync) build output files to martin ssh ${RSYNCUSER}@${MARTIN} mkdir -p $BUILD_DIR ssh ${RSYNCUSER}@${MARTIN} mkdir -p $BUILD_DIR/rawimg ssh ${RSYNCUSER}@${MARTIN} mkdir -p $BUILD_DIR/mfgbld rsync -avz --exclude=iceboy* --exclude=changeling* --exclude=stca* --exclude=yocto* $BUILD_OUTPUT_DIR/build/il/packages/fsimg ${RSYNCUSER}@${MARTIN}:$BUILD_DIR rsync -avz $BUILD_OUTPUT_DIR/build/il/packages/fsimg/yocto_version ${RSYNCUSER}@${MARTIN}:$BUILD_DIR/fsimg rsync -avz $BUILD_OUTPUT_DIR/build/il/packages/fsimg/yocto_signature ${RSYNCUSER}@${MARTIN}:$BUILD_DIR/fsimg rsync -avz $BUILD_OUTPUT_DIR/build/il/packages/rawimg/qemu*.img.gz ${RSYNCUSER}@${MARTIN}:$BUILD_DIR/rawimg rsync -avz $BUILD_OUTPUT_DIR/build/il/packages/idltar/p2CliTarBall.tar ${RSYNCUSER}@${MARTIN}:$BUILD_DIR/mfgbld/p2CliTarBall.tar rsync -avz $BUILD_OUTPUT_DIR/.changelists ${RSYNCUSER}@${MARTIN}:$BUILD_DIR rsync -avz $BUILD_OUTPUT_DIR/IL_*.txt ${RSYNCUSER}@${MARTIN}:$BUILD_DIR # ssh ${RSYNCUSER}@${MARTIN} mkdir -p $BUILD_DIR/chassisvm # ssh ${RSYNCUSER}@${MARTIN} mkdir -p $BUILD_DIR/stca
ssh ${RSYNCUSER}@${MARTIN} mkdir -p $BUILD_DIR/rawimg rsync -avz $BUILD_OUTPUT_DIR/build/il/packages/rawimg/qemu*.img.gz ${RSYNCUSER}@${MARTIN}:$BUILD_DIR/rawimg
# -- transfer (via rsync) build output files to martin rsync -avz $BUILD_OUTPUT_DIR/build/il/packages/fsimg/stca* scm@martin.spirentcom.com:$BUILD_DIR/fsimg #rsync -avz $BUILD_OUTPUT_DIR/build/il/packages/fsimg/changeling* scm@martin.spirentcom.com:$BUILD_DIR/fsimg rsync -avz $BUILD_OUTPUT_DIR/build/il/packages/fsimg/yocto* scm@martin.spirentcom.com:$BUILD_DIR/fsimg rsync -avz --exclude=rawimage.map --exclude=qemu*.img.gz $BUILD_OUTPUT_DIR/build/il/packages/rawimg scm@martin.spirentcom.com:$BUILD_DIR rsync -avz --exclude=rpm* --exclude=spec* $BUILD_OUTPUT_DIR/build/il/packages/stca scm@martin.spirentcom.com:$BUILD_DIR # rsync -avz $BUILD_OUTPUT_DIR/build/il/packages/mfgbld scm@martin.spirentcom.com:$BUILD_DIR rsync -avz $BUILD_OUTPUT_DIR/build/il/packages/bundle scm@martin.spirentcom.com:$BUILD_DIR ssh scm@martin.spirentcom.com mkdir -p $BUILD_DIR/chassisvm ssh scm@martin.spirentcom.com chmod 777 $BUILD_DIR/mfgbld ssh scm@martin.spirentcom.com chmod 777 $BUILD_DIR/mfgbld/*
