hudson.tasks.Shell.command:
#!/bin/bash
if [ -f /mnt/builds/jenkins/il_changeling_all_yocto-${BRANCH_NAME}_promoting.txt ]; then echo "Promotion is in progress, wait for 5 minutes" sleep 300 else echo "No promotion is in progress, proceeding with packaging" fi
echo "Packaging started" > /mnt/builds/jenkins/il_changeling_all_yocto-${BRANCH_NAME}_packaging.txt echo Copying il_changeling_all build artifacts from /mnt/builds/jenkins/il_changeling_all_yocto-$BRANCH_NAME/build-packaging/ ls -algh /mnt/builds/jenkins/il_changeling_all_yocto-$BRANCH_NAME/build-packaging/*.txt mkdir -p $WORKSPACE/build/il/packages cp -r /mnt/builds/jenkins/il_changeling_all_yocto-$BRANCH_NAME/build-packaging/* $WORKSPACE/build/il/packages cp $WORKSPACE/build/il/packages/fsimg/rawimage.map $WORKSPACE/build/il/packages/fsimg/changeling_rawimage.map echo "Finished copying il_changeling_all files" rm -f /mnt/builds/jenkins/il_changeling_all_yocto-${BRANCH_NAME}_packaging.txt

